mapper.select:
select nonce.id, nonce.active_nonce, nonce.revoked, nonce.spent, nonce.expires_at, nonce.created_at, nonce_type.id as nonce_type_id, nonce_type.name as nonce_type_name, nonce_type.seconds_to_expiry as nonce_type_seconds_to_expiry, nonce_type.created_at as nonce_type_created_at,
resource_owner.id as resource_owner_id, resource_owner.email as resource_owner_email, resource_owner.active_password as resource_owner_active_password, resource_owner.email_verified as resource_owner_email_verified, resource_owner.created_at as resource_owner_created_at
from nonce join nonce_type on nonce_type.id = nonce.nonce_type_id join resource_owner on resource_owner.id = nonce.resource_owner_id where nonce.active_nonce = #{nonce}

mapper.update:
update nonce
set spent = true
where id = #{id}
